import { motion, useScroll, useTransform } from 'framer-motion';
import { useRef } from 'react';

const AnimatedBackground = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ["start start", "end start"]
  });

  const backgroundY = useTransform(scrollYProgress, [0, 1], ["0%", "100%"]);
  const opacity = useTransform(scrollYProgress, [0, 0.5, 1], [0.5, 0.3, 0.5]);

  return (
    <motion.div
      ref={ref}
      className="fixed inset-0 z-0"
      style={{
        opacity,
        background: `
          radial-gradient(circle at 50% ${backgroundY}, 
            rgba(59, 130, 246, 0.15), 
            transparent 50%),
          radial-gradient(circle at 80% ${backgroundY}, 
            rgba(147, 51, 234, 0.15), 
            transparent 50%)
        `,
      }}
    >
      <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-40" />
    </motion.div>
  );
};

export default AnimatedBackground;